import { Component } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule, NgForm } from '@angular/forms';
import { ProductServiceService } from '../../product-service.service';

@Component({
  selector: 'app-add-product',
  standalone: true,
  imports: [CommonModule, FormsModule],
  templateUrl: './add-product.component.html',
  styleUrl: './add-product.component.css'
})
export class AddProductComponent {
  constructor(private productService: ProductServiceService) {}

  onSubmit(form: NgForm) {
    if (form.valid) {
      this.productService.addProduct(form.value).subscribe({
        next: (res) => {
          console.log('Product added:', res);
          form.reset();
        },
        error: (err) => console.error('Error adding product:', err)
      });
    }
  }
}
<h2>Add Product</h2>
<form #productForm="ngForm" (ngSubmit)="onSubmit(productForm)">
  <label for="title">Title:</label>
  <input id="title" name="title" ngModel required />
  <br />
  <label for="category">Category:</label>
  <input id="category" name="category" ngModel required />
  <br />
  <label for="price">Price:</label>
  <input id="price" name="price" type="number" ngModel required />
  <br />
  <button type="submit" [disabled]="!productForm.valid">Add Product</button>
</form>
<p>add-product works!</p>
