import { Component, OnInit } from '@angular/core';
import { ProductServiceService } from '../../product-service.service';
@Component({
  selector: 'app-product-component',
  standalone: true,
  imports: [],
  templateUrl: './product-component.component.html',
  styleUrl: './product-component.component.css'
})
export class ProductComponentComponent implements OnInit {
  products: any;
  constructor(private productService: ProductServiceService) {}
  // ngOnInit() {
  //   this.productService.getFilteredProducts().subscribe({
  //     next: (res: any) => this.products = res.products,
  //     error: (err: any) => console.error(err)
  //   });
  // }

  ngOnInit(): void {
    this.productService.getFilteredProducts().subscribe(res => {
      this.products = res.products.filter(
        (p: any) => p.category === 'beauty' && p.price > 9
      );
    });
  }
}
